
SELECT COUNT(*) FROM cast_info AS ci,company_name AS cn,movie_companies AS mc,title AS t WHERE cn.country_code = '[jp]' AND t.production_year BETWEEN 2006 AND 2007 AND ci.note = '(voice: English version)' AND mc.note LIKE '%(Japan)%' AND mc.note NOT LIKE '%(USA)%' AND ci.movie_id = t.id AND ci.movie_id = mc.movie_id AND t.id = mc.movie_id AND mc.company_id = cn.id