
SELECT COUNT(*) FROM company_name AS cn,company_type AS ct,info_type AS it,kind_type AS kt,movie_companies AS mc,movie_info AS mi,movie_info_idx AS miidx,title AS t WHERE cn.country_code = '[us]' AND ct.kind = 'production companies' AND it.info = 'rating' AND kt.kind = 'movie' AND mi.movie_id = t.id AND mi.movie_id = miidx.movie_id AND mi.movie_id = mc.movie_id AND t.kind_id = kt.id AND t.id = mc.movie_id AND t.id = miidx.movie_id AND mc.company_id = cn.id AND mc.company_type_id = ct.id AND mc.movie_id = miidx.movie_id AND miidx.info_type_id = it.id