
SELECT COUNT(*) FROM cast_info AS ci,company_name AS cn,company_type AS ct,movie_companies AS mc,title AS t WHERE t.production_year > 1990 AND ci.note LIKE '%(producer)%' AND cn.country_code = '[us]' AND t.id = mc.movie_id AND t.id = ci.movie_id AND mc.movie_id = ci.movie_id AND mc.company_id = cn.id AND mc.company_type_id = ct.id