
SELECT COUNT(*) FROM company_type AS ct,info_type AS it,movie_companies AS mc,movie_info_idx AS mi_idx,title AS t WHERE t.production_year > 2000 AND mc.note NOT LIKE '%(as Metro-Goldwyn-Mayer Pictures)%' AND ct.kind = 'production companies' AND it.info = 'bottom 10 rank' AND ct.id = mc.company_type_id AND mc.movie_id = t.id AND mc.movie_id = mi_idx.movie_id AND t.id = mi_idx.movie_id AND mi_idx.info_type_id = it.id