
SELECT COUNT(*) FROM company_name AS cn,company_type AS ct,keyword AS k,kind_type AS kt,movie_companies AS mc,movie_info AS mi,movie_info_idx AS mi_idx,movie_keyword AS mk,title AS t WHERE t.production_year > 2005 AND k.keyword IN ('murder', 'murder-in-title', 'blood', 'violence') AND mi_idx.info < '8.5' AND cn.country_code != '[us]' AND kt.kind IN ('movie', 'episode') AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Danish', 'Norwegian', 'German', 'USA', 'American') AND kt.id = t.kind_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mi_idx.movie_id AND t.id = mc.movie_id AND mi.movie_id = mk.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mc.movie_id AND mk.movie_id = mi_idx.movie_id AND mk.movie_id = mc.movie_id AND mk.keyword_id = k.id AND mi_idx.movie_id = mc.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id