
SELECT COUNT(*) FROM cast_info AS ci,keyword AS k,movie_info AS mi,movie_info_idx AS mi_idx,movie_keyword AS mk,title AS t WHERE k.keyword IN ('murder', 'blood', 'gore', 'death', 'female-nudity') AND ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND mi.info = 'Horror' AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND mi.movie_id = ci.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mk.movie_id AND mi_idx.movie_id = ci.movie_id AND mi_idx.movie_id = mk.movie_id AND ci.movie_id = mk.movie_id AND mk.keyword_id = k.id