
SELECT COUNT(*) FROM cast_info AS ci,comp_cast_type AS cct1,company_name AS cn,complete_cast AS cc,keyword AS k,movie_companies AS mc,movie_info AS mi,movie_keyword AS mk,title AS t WHERE t.production_year BETWEEN 2000 AND 2010 AND cct1.kind = 'cast' AND k.keyword = 'computer-animation' AND ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND cn.country_code = '[us]' AND mi.info IS NOT NULL AND t.id = mi.movie_id AND t.id = mc.movie_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND t.id = cc.movie_id AND mi.movie_id = mc.movie_id AND mi.movie_id = ci.movie_id AND mi.movie_id = mk.movie_id AND mi.movie_id = cc.movie_id AND mc.movie_id = ci.movie_id AND mc.movie_id = mk.movie_id AND mc.movie_id = cc.movie_id AND mc.company_id = cn.id AND ci.movie_id = mk.movie_id AND ci.movie_id = cc.movie_id AND mk.movie_id = cc.movie_id AND mk.keyword_id = k.id AND cc.subject_id = cct1.id