
SELECT COUNT(*) FROM keyword AS k,movie_info AS mi,movie_keyword AS mk,title AS t WHERE k.keyword LIKE '%sequel%' AND t.production_year > 1990 AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German', 'USA', 'American') AND t.id = mi.movie_id AND t.id = mk.movie_id AND mi.movie_id = mk.movie_id AND mk.keyword_id = k.id