
SELECT COUNT(*) FROM company_type AS ct,movie_companies AS mc,movie_info AS mi,title AS t WHERE t.production_year > 2010 AND mi.info IN ('USA', 'America') AND ct.kind = 'production companies' AND mc.note LIKE '%(VHS)%' AND mc.note LIKE '%(USA)%' AND mc.note LIKE '%(1994)%' AND t.id = mi.movie_id AND t.id = mc.movie_id AND mi.movie_id = mc.movie_id AND mc.company_type_id = ct.id